import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchTasks, addTask, updateTask, deleteTask } from '../redux/taskSlice';
import TaskForm from '../components/Taskform';
import TaskList from '../components/Tasklist';
import Filter from '../components/Filter';

const TaskPage = () => {
  const dispatch = useDispatch();
  const tasks = useSelector((state) => state.tasks.tasks);
  const [filterStatus, setFilterStatus] = useState('all');

  useEffect(() => {
    dispatch(fetchTasks());
  }, [dispatch]);

  const filteredTasks = tasks.filter((task) => {
    if (filterStatus === 'all') return true;
    return filterStatus === 'completed' ? task.completed : !task.completed;
  });

  const handleAddTask = (task) => {
    dispatch(addTask({ ...task, id: Date.now() }));
  };

  const handleUpdateTask = (task) => {
    dispatch(updateTask(task));
  };

  const handleDeleteTask = (id) => {
    dispatch(deleteTask(id));
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4 flex justify-center items-center">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md p-6 flex flex-col items-center">
        <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">Task Tracker</h2>
        <div className="space-y-6">
          <div className="bg-gray-50 p-4 rounded-md">
            <TaskForm onSubmit={handleAddTask} />
          </div>
          <div className="flex justify-center">
            <Filter filterStatus={filterStatus} setFilterStatus={setFilterStatus} />
          </div>
          <div className="mt-4">
            <TaskList
              tasks={filteredTasks}
              onUpdate={handleUpdateTask}
              onDelete={handleDeleteTask}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskPage;
