import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateTask } from '../redux/tasksSlice';
import { useNavigate, useParams } from 'react-router-dom';
import Header from '../components/Header';

const UpdateTaskPage = () => {
  const { id } = useParams();
  const tasks = useSelector((state) => state.tasks.tasks);
  const taskToEdit = tasks.find((task) => task.id === parseInt(id));
  const [title, setTitle] = useState(taskToEdit?.title || '');
  const [completed, setCompleted] = useState(taskToEdit?.completed || false);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleUpdate = (e) => {
    e.preventDefault();
    dispatch(updateTask({ id: parseInt(id), title, completed }));
    navigate('/tasks');
  };

  return (
    <div>
      <Header />
      <div className="container mt-5">
        <h2>Update Task</h2>
        <form onSubmit={handleUpdate}>
          <div className="form-group">
            <label>Task Title</label>
            <input
              type="text"
              className="form-control"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className="form-check mt-3">
            <input
              type="checkbox"
              className="form-check-input"
              checked={completed}
              onChange={() => setCompleted(!completed)}
            />
            <label className="form-check-label">Completed</label>
          </div>
          <button type="submit" className="btn btn-primary mt-3">Update Task</button>
        </form>
      </div>
    </div>
  );
};

export default UpdateTaskPage;
